import React, { useEffect, useState } from 'react';
import axios from "axios";
import { createUserRoute, filterContactsRoute } from '../../utils/APIRoutes';
import Contacts from './Contacts';
import styles from "./Sidebar.module.css";
import { useNavigate } from 'react-router-dom';
import profileImg from '../../assets/images/blank-profile-picture-973460_960_720.webp';
import ContactsContainer from './ContactsContainer';

function Sidebar({ currentChat, setCurrentChat }) {
  const navigate = useNavigate();
  const [searchValue, setSearchValue] = useState('');
  const [isClicked, setIsClicked] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [contacts, setContacts] = useState([]);

  useEffect(() => {

  })

  const handleOpenModal = () => {
    setShowModal(true);
  };
  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleChatChanged = (chat) => {
    setCurrentChat(chat)
  }

  const handleSearch = () => {
    setIsClicked(true);

  }

  const filter = async() => {
    // const response = await axios.get(`${filterContactsRoute}?search=${searchValue}`)
    let keyword = searchValue.toLowerCase();
    let filtered_users = contacts.filter(function (contact) {
      return contact.firstname.toLowerCase().includes(keyword) || contact.lastname.toLowerCase().includes(keyword);
    });
    setContacts(filtered_users);
  }

  return (
    <div className={styles.container}>
      <div className={styles.headerContainer}>
        <div>
          {localStorage.getItem("chat-app-user") ? <img src={profileImg} className={styles.profileImg} /> : null}
        </div>

        <div onClick={() => navigate('/login')} className={styles.login} >
          <button>Log in</button>
        </div>
      </div>


      {isClicked ?
        <>
          <div>
            <input placeholder='Search' value={searchValue}
              onChange={e => setSearchValue(e.target.value)}
              onClick={handleSearch} className={styles.searchInput}
              onKeyUp={filter} />
            <button className={styles.cancelBtn} onClick={() => setIsClicked(false)}>Cancel</button>

          </div>
          <ContactsContainer changeChat={handleChatChanged} handleOpenModal={handleOpenModal}
            showModal={showModal} handleCloseModal={handleCloseModal} contacts={contacts} setContacts={setContacts} />
        </>
        :
        <>
          <div>
            <input placeholder='Search chat' value={searchValue}
              onChange={e => setSearchValue(e.target.value)}
              onClick={handleSearch} className={styles.searchInput} />
          </div>
          <div>Chats...
            <Contacts changeChat={handleChatChanged} handleOpenModal={handleOpenModal}
              show={showModal} onClose={handleCloseModal} currentChat={currentChat} contacts={contacts} setContacts={setContacts} />
          </div>
        </>
      }


      {/* <Contacts /> */}

    </div>

  )
}

export default Sidebar
