import React, { useState } from 'react'
import Contacts from './Contacts';
import ModalWindow from './ModalWindow/ModalWindow';
import axios from 'axios';
import { createUserRoute } from '../../../utils/APIRoutes';

function ContactsContainer({ changeChat }) {
    const [showModal, setShowModal] = useState(false);

    const handleOpenModal = () => {
        setShowModal(true);
    };
    const handleCloseModal = () => {
        setShowModal(false);
    };

    const createChat = async (data) => {
        try {
            const response = await axios.post(createUserRoute, data);
            if (response.data.status === true) {
                console.log('yees: ', response.data.user);
            }
            onClose();

        } catch (error) {
            console.error('Error creating chat:', error);
        }
    };

    return (
        <div>
            <div>
                <button onClick={handleOpenModal}>Create new chat</button>
                <ModalWindow show={showModal} onClose={handleCloseModal} createChat={createChat} />
            </div>
            <Contacts changeChat={changeChat} />
        </div>
    )
}

export default ContactsContainer
