import React, { useEffect, useState } from 'react'
import axios from "axios";
import { getAllUserRoute } from '../../utils/APIRoutes';
import ModalWindow from './ModalWindow/ModalWindow';

function Contacts() {
    const [contacts, setContacts] = useState([]);
    const [showModal, setShowModal] = useState(false);

    const handleOpenModal = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    useEffect(() => {
        async function fetchData() {
            const data = await axios.get(getAllUserRoute)
            setContacts(data.data);
        }
        fetchData();
    }, [contacts])

    return ( < >
        <
        div >
        <
        button onClick = { handleOpenModal } > Create new chat < /button> <
        ModalWindow show = { showModal }
        onClose = { handleCloseModal }
        /> <
        /div> <
        div > {
            contacts.map((contact, index) => ( <
                p key = { index } > { contact.firstname } { contact.lastname } < /p>
            ))
        } <
        /div> <
        />
    )
}

export default Contacts
