import React from 'react';
import { useForm, FormProvider, useFormContext } from 'react-hook-form';
import styles from './ModalWindow.module.css';
import axios from 'axios';
import { createUserRoute } from '../../../utils/APIRoutes';

const Input = ({ name, placeholder }) => {
  const { register, formState: { errors } } = useFormContext();

  return (
    <div className={styles.formGroup}>
      <input
        name={name}
        placeholder={placeholder}
        {...register(name, {
          required: `${placeholder} is required`,
          minLength: {
            value: 3,
            message: `${placeholder} must be at least 3 characters`
          }
        })}
        className={`${styles.input} ${errors[name] ? styles.errorInput : ''}`}
      />
      {errors[name] && <span className={styles.errorText}>{errors[name].message}</span>}
    </div>
  );
};

const ModalWindow = ({ show, onClose }) => {
  const methods = useForm();

  if (!show) {
    return null;
  }

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const createChat = async (data) => {
    try {
      const response = await axios.post(createUserRoute, data);
      if (response.data.status === true) {
        console.log('yees: ', response.data.user);
      }
      onClose();
    } catch (error) {
      console.error('Error creating chat:', error);
    }
  };

  return (
    <div className={styles.modalBackdrop} onClick={handleBackdropClick}>
      <div className={styles.modalContent}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        <FormProvider {...methods}>
          <form onSubmit={methods.handleSubmit(createChat)} className={styles.content}>
            <Input name="firstname" placeholder="Firstname" />
            <Input name="lastname" placeholder="Lastname" />
            <button type="submit">Search</button>
          </form>
        </FormProvider>
      </div>
    </div>
  );
};

export default ModalWindow;
