import React, { useEffect, useState } from 'react'
import axios from "axios";
import { getAllUserRoute } from '../../utils/APIRoutes';
import ModalWindow from './ModalWindow/ModalWindow';

function Contacts() {
  const [contacts, setContacts] = useState([]);
  const [isClicked, setIsClicked] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  useEffect(() => {
    async function fetchData() {
      const data = await axios.get(getAllUserRoute)
      setContacts(data.data);
      console.log(data.data);
    }
    fetchData();
  }, [])

  return (<>
    {isClicked && <ModalWindow />}
    <div>
      <button onClick={() => setIsClicked(true)}>Create new chat</button>
    </div>
    <div>
      {contacts.map((contact, index) => (
        <p key={index}>{contact.firstname} {contact.lastname}</p>
      ))}
    </div>
  </>
  )
}

export default Contacts
