import React, { useEffect, useState } from 'react'
import axios from "axios";
import { deleteContactRoute, getAllUserRoute, updateContactRoute } from '../../utils/APIRoutes';
import ModalWindow from './ModalWindow/ModalWindow';
import styles from "./Sidebar.module.css";
import DeleteChat from './DeleteChat/DeleteChat';

function Contacts({ changeChat, contacts, setContacts }) {
  const [showModal, setShowModal] = useState(false);
  const [currentSelected, setCurrentSelected] = useState(undefined);

  useEffect(() => {
    const fetchData = async () => {
      const data = await axios.get(getAllUserRoute)
      setContacts(data.data);
    }
    fetchData();
  }, [contacts]);

  const changeCurrentChange = (index, contact) => {
    setCurrentSelected(index);
    changeChat(contact);
  }

  const deleteChat = async (contact) => {
    if (contact) {
      await axios.delete(deleteContactRoute, { data: { contact } });
    }
  }

  const handleOpenModal = () => {
    setShowModal(true);
  };
  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (<>
    <div className={styles.contacts}>
      {contacts.map((contact, index) => {
        return <div>
          <div key={index} className={`${styles.contact} ${index === currentSelected ? styles.selected : ''} `}
            onClick={() => changeCurrentChange(index, contact)}>
            {contact.firstname} {contact.lastname}
          </div>
          <button onClick={handleOpenModal}>Delete</button>
          <DeleteChat show={showModal} onClose={handleCloseModal} deleteChat={deleteChat} contact={contact} />
        </div>
      })}
    </div>
  </>
  )
}

export default Contacts
