import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from './Register.module.css';
import { useForm, FormProvider, useFormContext } from 'react-hook-form';

const Input = ({ name, placeholder, type, validate }) => {
    const { register, formState: { errors } } = useFormContext();
    return (
        <div className={styles.formGroup}>
            <input
                type={type}
                name={name}
                placeholder={placeholder}
                {...register(name, validate)}
                className={`${styles.input} ${errors[name] ? styles.errorInput : ''}`}
            />
            <div>
                {errors[name] && <span className={styles.errorText}>{errors[name].message}</span>}
            </div>
        </div>
    );
};

function Register() {
    const [values, setValues] = useState({
        firstname: '',
        lastname: '',
        email: '',
        password: '',
        confirmPassword: ''
    });
    const methods = useForm();

    const handleSubmit = (data, event) => {
        console.log(event);
        console.log(data);
        // event.preventDefault();
        alert("form");
    }



    const handleChange = (event) => {
        setValues({ ...values, [event.target.name]: event.target.value })
    }

    return (
        <div className={styles.container}>
            <FormProvider {...methods}>
                <form className={styles.formContainer} onSubmit={methods.handleSubmit(handleSubmit)} >
                    <h3>Registration</h3>
                    <Input type="text"
                        name={'firstname'}
                        placeholder='Firstname'
                        validate={{
                            required: 'Firstname is required',
                            minLength: { value: 3, message: 'Firstname must be at least 3 characters' }
                        }}
                        on />
                    <Input type="text"
                        name={'lastname'}
                        placeholder='Lastname'
                        validate={{
                            required: 'Lastname is required',
                            minLength: { value: 3, message: 'Lastname must be at least 3 characters' }
                        }} />
                    <Input type="email"
                        name={'email'}
                        placeholder='Email'
                        validate={{
                            required: 'Email is required',
                            pattern: {
                                value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
                                message: 'Enter a valid email'
                            }
                        }} />
                    <Input type="password"
                        name={'password'}
                        placeholder='Password'
                        validate={{
                            required: 'Password is required',
                            minLength: { value: 6, message: 'Password must be at least 6 characters' }
                        }} />
                    <Input type="password"
                        name={'confirmPassword'}
                        placeholder='Confirm Password'
                        validate={{
                            required: 'Confirm Password is required',
                        }} />
                    <button type='submit'> Create User </button>
                    <span> already have an account ? <Link to='/login'> Login </Link> </span>
                </form>
            </FormProvider>
        </div>
    )
}

export default Register
