import React, { useEffect, useState } from 'react'
import axios from "axios";
import { getAllUserRoute } from '../../utils/APIRoutes';
import ModalWindow from './ModalWindow/ModalWindow';
import styles from "./Sidebar.module.css";

function Contacts({ changeChat }) {
  const [contacts, setContacts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentSelected, setCurrentSelected] = useState(undefined);

  useEffect(() => {
    async function fetchData() {
      const data = await axios.get(getAllUserRoute)
      setContacts(data.data);
    }
    fetchData();
  }, [contacts]);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const changeCurrentChange = (index, contact) => {
    console.log(index, currentSelected)
    setCurrentSelected(index);
    changeChat(contact);
  }


  return (<>
    <div>
      <button onClick={handleOpenModal}>Create new chat</button>
      <ModalWindow show={showModal} onClose={handleCloseModal} />
    </div>
    <div className={styles.contacts}>
      {contacts.map((contact, index) => {
        return <>
          <div className={index === currentSelected ? styles.selected : ''} key={index}
            onClick={() => changeCurrentChange(index, contact)}>
            {contact.firstname} {contact.lastname}
          </div>

        </>

      })}
    </div>
  </>
  )
}

export default Contacts
