import React, { useEffect, useState } from 'react';
import axios from "axios";
import { createUserRoute } from '../../utils/APIRoutes';
import Contacts from './Contacts';
import styles from "./Sidebar.module.css";

function Sidebar() {
  const [searchValue, setSearchValue] = useState('');
  const [isClicked, setIsClicked] = useState(false);
  const [currentChat, setCurrentChat] = useState(undefined);

  const handleChatChanged = (chat) => {
    setCurrentChat(chat)
  }

  const handleClick = () => {
    setIsClicked(true);
  }

  return (
    <div className={styles.container}>
      <div>Log in</div>

      {isClicked ?
        <>
          <div>
            <input placeholder='Search' value={searchValue}
              onChange={e => setSearchValue(e.target.value)}
              onClick={handleClick} className={styles.searchInput} />
            <button className={styles.cancelBtn} onClick={() => setIsClicked(false)}>Cancel</button>

          </div>
          <Contacts changeChat={handleChatChanged} />
        </>
        :
        <>
          <div>
            <input placeholder='Search chat' value={searchValue}
              onChange={e => setSearchValue(e.target.value)}
              onClick={handleClick} className={styles.searchInput} />
          </div>
          <div>Chats...</div>
        </>
      }


      {/* <Contacts /> */}

    </div>

  )
}

export default Sidebar
