import React, { useEffect, useState } from 'react'
import axios from "axios";
import { getAllUserRoute, updateContactRoute } from '../../utils/APIRoutes';
import ModalWindow from './ModalWindow/ModalWindow';
import styles from "./Sidebar.module.css";

function Contacts({ changeChat }) {
  const [contacts, setContacts] = useState([]);
  const [currentSelected, setCurrentSelected] = useState(undefined);

  useEffect(() => {
    async function fetchData() {
      const data = await axios.get(getAllUserRoute)
      setContacts(data.data);
    }
    fetchData();
  }, [contacts]);

  const changeCurrentChange = (index, contact) => {
    setCurrentSelected(index);
    changeChat(contact);
  }

  const updateContact = async (contact) => {
    // const response = await axios.put(updateContactRoute, { contact });

  }

  return (<>
    <div className={styles.contacts}>
      {contacts.map((contact, index) => {
        return <div>
          <div key={index} className={`${styles.contact} ${index === currentSelected ? styles.selected : ''} `}
            onClick={() => changeCurrentChange(index, contact)}>
            {contact.firstname} {contact.lastname}
            <button onClick={handleOpenModal}>Create new chat</button>
            <ModalWindow show={showModal} onClose={handleCloseModal} createChat={createChat} />
          </div>
        </div>
      })}
    </div>
  </>
  )
}

export default Contacts
