import React, { useEffect, useState } from 'react';
import axios from "axios";
import { createUserRoute } from '../../utils/APIRoutes';
import Contacts from './Contacts';
import styles from "./Sidebar.module.css";

function Sidebar() {
  const [searchValue, setSearchValue] = useState('');
  const [isClicked, setIsClicked] = useState(false);
  const [inputValues, setInputValues] = useState({
    firstname: '',
    lastname: ''
  });

  useEffect(() => {
    console.log(searchValue);
  }, [searchValue]);

  let createChat = async (e) => {
    const { firstname, lastname } = inputValues
    const { data } = await axios.post(createUserRoute, {
      firstname, lastname
    });
    if (data.status === true) {
      console.log('yees: ', data.user);
    }
  }

  const handleChange = (event) => {
    setInputValues({ ...inputValues, [event.target.name]: event.target.value })
  }

  const handleClick = () => {
    setIsClicked(true);
  }

  return (
    <div className={styles.container}>
      <div>Log in</div>
      {isClicked ?
        <Contacts />

        : <div>
          <input placeholder='Search chat' value={searchValue}
            onChange={e => setSearchValue(e.target.value)}
            onClick={handleClick} />
        </div>}


      {/* <Contacts /> */}
      {/* <div>
        <input name='firstname' placeholder='Input firstname' onChange={e => handleChange(e)} />
        <input name='lastname' placeholder='Input lastname' onChange={e => handleChange(e)} />
        <button onClick={createChat}>Search</button>
        <button onClick={() => setIsClicked(false)}>Cancel</button>
      </div> */}
    </div>

  )
}

export default Sidebar
