import React, { useEffect, useState } from 'react';
import axios from "axios";
import { createUserRoute } from '../../utils/APIRoutes';
import Contacts from './Contacts';

function Sidebar() {
  const [inputValues, setInputValues] = useState({
    firstname: '',
    lastname: ''
  });

  useEffect(() => {
    console.log(inputValues);
  }, [inputValues]);

  let createChat = async (e) => {
    const { firstname, lastname } = inputValues
    const { data } = await axios.post(createUserRoute, {
      firstname, lastname
    });
    if (data.status === true) {
      console.log('yees: ', data.user);
    }
  }

  const handleChange = (event) => {
    setInputValues({ ...inputValues, [event.target.name]: event.target.value })
  }

  return (
    <div>
      <div>Log in</div>
      <div>
        <input placeholder='Search chat' />

        <input name='firstname' placeholder='Input firstname' onChange={e => handleChange(e)} />
        <input name='lastname' placeholder='Input lastname' onChange={e => handleChange(e)} />
        <button onClick={createChat}>Search</button>
      </div>
      <div> Chats...</div>
      <Contacts />
    </div>
  )
}

export default Sidebar
