import React, {useState} from 'react';
import styles from './ModalWindow.module.css';
import axios from 'axios';
import { createUserRoute } from '../../../utils/APIRoutes';


const ModalWindow = ({ show, onClose }) => {
    const [inputValues, setInputValues] = useState({
        firstname: '',
        lastname: ''
    });
    if (!show) {
        return null;
    }

    const handleBackdropClick = (e) => {
        if (e.target === e.currentTarget) {
            onClose();
        }
    };

    let createChat = async (e) => {
        const { firstname, lastname } = inputValues
        const { data } = await axios.post(createUserRoute, {
            firstname, lastname
        });
        if (data.status === true) {
            console.log('yees: ', data.user);
        }
    }

    const handleChange = (event) => {
        setInputValues({ ...inputValues, [event.target.name]: event.target.value })
    }

    return (
        <div className={styles.modalBackdrop} onClick={handleBackdropClick}>
            <div className={styles.modalContent}>
                <span className={styles.closeButton} onClick={onClose}>&times;</span>
                <div className={styles.content}>
                    <input name='firstname' placeholder='Input firstname' onChange={e => handleChange(e)} />
                    <input name='lastname' placeholder='Input lastname' onChange={e => handleChange(e)} />
                    <button onClick={createChat}>Search</button>
                </div>
            </div>
        </div>
    );
};

export default ModalWindow;
